import React, { useState } from 'react';
import './Foros.css';

const topicsData = [
  { id: 1, title: 'Que hago con esto?', content: 'Tengo cinco tablas de madera de olijarde....', date: 'Hace 8 d칤as', tag: 'Reutilizaci칩n' },
  { id: 2, title: 'Motores DC juguetes', content: 'Body text for whatever you\'d like to say.', date: 'Hace 7 d칤as' },
  { id: 3, title: 'Reutilidad de libros', content: 'Body text for whatever you\'d like to say.', date: 'Hace 6 d칤as' },
  { id: 4, title: 'Es mejor donar o vender?', content: 'Body text for whatever you\'d like to say.', date: 'Hace 3 d칤as' },
  { id: 5, title: 'Peores reutilidades', content: 'Body text for whatever you\'d like to say.', date: 'Hace 8 d칤as' },
  { id: 6, title: 'Mejores piezas de pc', content: 'Body text for whatever you\'d like to say.', date: 'Hace 5 d칤as' },
  { id: 7, title: 'Los productos son de calidad ?', content: 'Body text for whatever you\'d like to say.', date: 'Hace 10 d칤as' },
  { id: 8, title: 'Objetos usuales da침ados', content: 'Body text for whatever you\'d like to say.', date: 'Hace 2 d칤as' },
  { id: 9, title: 'Ropa de segunda', content: 'Body text for whatever you\'d like to say.', date: 'Hace 12 d칤as' },
];

const Foros = () => {
  const [search, setSearch] = useState('');

  const filteredTopics = topicsData.filter(topic =>
    topic.title.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div className="forum-container">
      <div className="forum-header">
        <input
          type="text"
          placeholder="Buscar"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
        <span className="search-icon">游댌</span>
      </div>

      <div className="forum-grid">
        {filteredTopics.map((topic) => (
          <div key={topic.id} className="forum-card">
            <div className="forum-image-placeholder" />
            <div className="forum-content">
              <h3>{topic.title}</h3>
              <p>{topic.content}</p>
              <div className="forum-footer">
                <span>{topic.date}</span>
                {topic.tag && <span className="forum-tag">{topic.tag}</span>}
              </div>
            </div>
          </div>
        ))}
      </div>

      <div className="forum-pagination">
        <button>&larr; Anterior</button>
        <span className="page-numbers">
          <strong>1</strong> 2 3 ... 67 68
        </span>
        <button>Siguiente &rarr;</button>
      </div>
    </div>
  );
};

export default Foros;
