import React from 'react';
import './Products.css';

const Products = () => {
  return (
    <div className="products-page">
      
      {/* Barra lateral de filtros */}
      <aside className="filters">
        <h3>Palabras claves</h3>
        {/* Aquí podrías poner checkboxes o categorías más adelante */}
      </aside>

      {/* Contenido principal */}
      <main className="product-list">
        
        {/* Barra de búsqueda */}
        <div className="search-bar">
          <input type="text" placeholder="Buscar productos..." />
        </div>

        {/* Grid de productos */}
        <div className="products-grid">
          {/* Producto de ejemplo - Venta */}
          <div className="product-card">
            <img src="ruta_imagen" alt="Blusa" className="product-image" />
            <span className="tag venta">Venta</span>
            <h4 className="product-title">Blusa</h4>
            <p className="product-price">$50</p>
          </div>

          {/* Producto de ejemplo - Donación */}
          <div className="product-card">
            <img src="ruta_imagen" alt="Reloj" className="product-image" />
            <span className="tag donacion">Donación</span>
            <h4 className="product-title">Reloj</h4>
            <p className="product-price">GRATIS</p>
          </div>
        </div>

        {/* Paginación */}
        <div className="pagination">
          {/* Aquí luego puedes agregar botones: «Anterior», números, «Siguiente» */}
        </div>

      </main>
      
    </div>
  );
};

export default Products;
