import React, { useState } from 'react';
import './ProductModal.css';

const ProductModal = ({ product, onClose, onSubmit, setNewProduct, onEdit, onDelete }) => {
  const esModoCrear = !!onSubmit && !!setNewProduct;
  const esEditable = product?.id > 4;

  const [editMode, setEditMode] = useState(false);
  const [editableProduct, setEditableProduct] = useState(product ? { ...product } : {});

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (esModoCrear) {
      setNewProduct({ ...product, [name]: value });
    } else {
      setEditableProduct({ ...editableProduct, [name]: value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (esModoCrear) {
      onSubmit();
    } else {
      onEdit(editableProduct);
    }
    onClose();
  };

  if (!product) return null; // Esto se coloca después de los hooks

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <button className="close-btn" onClick={onClose}>✖</button>

        {esModoCrear || editMode ? (
          <form onSubmit={handleSubmit}>
            <h2>{esModoCrear ? 'Agregar Producto' : 'Editar Producto'}</h2>
            <label>Nombre:
              <input type="text" name="name" value={esModoCrear ? product.name : editableProduct.name} onChange={handleChange} />
            </label>
            <label>Precio:
              <input type="number" name="price" value={esModoCrear ? product.price : editableProduct.price} onChange={handleChange} />
            </label>
            <label>Imagen URL:
              <input type="text" name="image" value={esModoCrear ? product.image : editableProduct.image} onChange={handleChange} />
            </label>
            <label>Descripción:
              <input type="text" name="description" value={esModoCrear ? product.description : editableProduct.description} onChange={handleChange} />
            </label>
            <label>Estado:
              <input type="text" name="estado" value={esModoCrear ? product.estado : editableProduct.estado} onChange={handleChange} />
            </label>
            <label>Serie:
              <input type="text" name="serie" value={esModoCrear ? product.serie : editableProduct.serie} onChange={handleChange} />
            </label>
            <label>Categoría:
              <input type="text" name="category" value={esModoCrear ? product.category : editableProduct.category} onChange={handleChange} />
            </label>
            <label>Descripción larga:
              <textarea name="longDescription" value={esModoCrear ? product.longDescription : editableProduct.longDescription} onChange={handleChange} />
            </label>
            <button type="submit">{esModoCrear ? 'Agregar Producto' : 'Guardar Cambios'}</button>
          </form>
        ) : (
          <>
            <div className="modal-left">
              <img src={product.image} alt={product.name} />
            </div>
            <div className="modal-right">
              <h2>{product.name}</h2>
              <span className="tag">{product.price === 0 ? 'Donación' : 'Venta'}</span>
              <h3>{product.price === 0 ? 'GRATIS' : `$${product.price.toLocaleString()}`}</h3>
              <p>Descripción: {product.description || 'Sin descripción'}</p>

              <div className="selectors">
                <label>Estado:</label>
                <span className="tagestado">{product.estado}</span>

                <label>Serie:</label>
                <span className="tagestado">{product.serie}</span>
              </div>

              <button className="action-btn">Agregar al carrito</button>

              <details>
                <summary>Descripción</summary>
                <p>{product.longDescription || 'Aquí se puede ampliar más información del producto.'}</p>
              </details>

              {esEditable && (
                <div className="edit-delete-buttons">
                  <button onClick={() => setEditMode(true)}>Editar</button>
                  <button onClick={() => onDelete(product.id)}>Eliminar</button>
                </div>
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default ProductModal;
